import React, { useState } from 'react';
import { Container, Card, Accordion,ListGroup, Form, FormControl, Button } from 'react-bootstrap';
import { FaArrowLeft, FaArrowRight } from 'react-icons/fa'
import { useHistory } from 'react-router-dom';
import api from '../../services/api';

export default function Corredores(props) {

    const codigo = props.location.state.codigo;
    const history = useHistory();

    const [filtro, setFiltro] = useState(busca);
    const [paradas, setParadas] = useState([]);
    const [value, setValue] = useState('');

    function handleGoBack() {
        history.goBack();
    }

    function onClickCorredor(codigo){
        setParadas([]);
        api.get(`Parada/BuscarParadasPorLinha?codigoLinha=${codigo}`)
            .then(response => {
                setParadas(response.data);
            })
            .catch(function(error){
                //erro
            })
    }

    function onChange(e) {
        setValue(e.target.value);
        if (e.target.value.length > 0) {
            setFiltro(busca.filter(item => { return item.lt.match(e.target.value) }))
        } else {
            setFiltro(busca);
        }
    }

    return (
        <Container style={{
            display: "flex",
            justifyContent: "center"
        }}>
            <Card body style={{
                width: "80%",
                minHeight: "80vh",
                marginTop: 30,
                marginBottom: 30,

            }}>
                <Card.Title>
                    <Container style={{
                        display: "flex",
                        justifyContent: "space-between"
                    }}>
                        <div style={{
                            cursor: "pointer"
                        }}
                            onClick={handleGoBack}
                        >
                            <FaArrowLeft />
                        </div>
                        <h3>Buscar por Corredores</h3>
                        <div></div>
                    </Container>
                </Card.Title>

                <Container
                    style={{
                        display: "flex",
                        justifyContent: "center"
                    }}>
                    <Card style={{ width: '80%' }}>
                        <Card.Header>
                            <Form inline >
                                <FormControl
                                    type="text"
                                    placeholder="filtrar"
                                    className="mr-sm-2"
                                    value={value}
                                    onChange={onChange}
                                />
                            </Form>
                        </Card.Header>

                <p>{codigo}</p>
                    </Card>
                </Container>


            </Card>
        </Container>
    )
}
