import React, { useState, useEffect } from 'react';
import { Button, Form, FormControl } from 'react-bootstrap';

export default function SearchVeiculo(props) {
    const { veiculos, setVeiculoBuscado, mostrarVeiculos, setMostrarVeiculos } = props;
    const [value, setValue] = useState('');

    function onSubmit(e){
        e.preventDefault();
        if(value === ""){
            return;
        }
        var flag = 0;
        veiculos.forEach(v => {
            console.log(v.c)
            if (v.c.toLowerCase() == value.toLowerCase() || v.cl == value) {
                setVeiculoBuscado(v);
                flag = 1;
            }
        })
        if(!flag){
            setVeiculoBuscado(null);
            alert("Informe um valor válido!")
        }
        setValue('');
    }

    return (
        <>
            <Form inline onSubmit={onSubmit}>
                <FormControl 
                    type="text" 
                    placeholder="Buscar veículos" 
                    className="mr-sm-2" 
                    value={value}
                    onChange={(e) => {setValue(e.target.value)}}
                />
                <Button variant="outline-success" type="submit">Buscar</Button>
                <Button variant="outline-success" onClick={() => {
                    setMostrarVeiculos(!mostrarVeiculos);
                }} >{mostrarVeiculos ? "Ocultar" : "Mostrar todos"}</Button>
            </Form>
        </>
    )
}
