import React from 'react';
import {Container, Col, Row,Card, Form, FormControl, Button} from 'react-bootstrap';
import JumbotronComponent from './components/Jumbotron';
import CardSearch from './components/CardSearch';
import CardDropdown from './components/CardDropdown';
import './styles.css'

export default function Section1() {
    return(
        <div style={{
            width: "100%",
            margin: 0,
        }} className="section-1">
            <Container style={{
                marginTop: 30,
            }}>
                <JumbotronComponent/>
                <Container>
                <Row style={{
                    display: "flex",
                    justifyContent: "space-between",                    
                }}>
                <Col
                    md={4}
                    sm={12}
                >
                <CardSearch
                    title="Buscar por linhas"
                    placeholder="Buscar linha"
                    path="/linhas"
                    linhas={true}
                />
                </Col>
                <Col
                    md={4}
                    sm={12}
                >
                <CardSearch
                    title="Buscar por paradas"
                    placeholder="Buscar parada"
                    path="/paradas"
                    paradas={true}
                />
                </Col>
                <Col
                    md={4}
                    sm={12}
                >
                <CardDropdown
                    title="Buscar por corredores"
                    path="/corredores"
                />
                </Col>
                </Row>
                </Container>
            </Container>
        </div>
    )
}
