import React, {useState} from 'react';
import {Form, FormControl, Button} from 'react-bootstrap';
import {FaPlus, FaMinus} from 'react-icons/fa'
import api from '../../../../../../../../services/api'

export default function Search(props){
    const { setParadas, veiculo, mostrarParadas, setMostrarParadas} = props;
    const [value, setValue] = useState('');
    const [selectValue, setSelectValue] = useState('linha');

    const [exibirFiltros, setExibirFiltros] = useState(false);

    function onSubmit(e){
        e.preventDefault();
        if(exibirFiltros){

        api.get(
            selectValue == "linha" ?
            `Parada/BuscarParadasPorLinha?codigoLinha=${value}`
            :
            `Parada/BuscarParadasPorCorredor?codigoCorredor=${value}`
        ).then(response => {
            if(response.data.length > 0 ){
                setParadas(response.data);
            }else{
                alert("Nenhuma informação encontrada!");
                return;
            }
        }).catch(function(error){
            //error
        })

    }else{
        api.get(`Parada/Buscar?termosBusca=${value}`)
            .then(response => {
                if(response.data.length > 0 ){
                    setParadas(response.data);
                }else{
                    alert("Nenhuma informação encontrada!");
                    return;
                }
            })
            .catch(function(error){
                //error
            })
        }
        setValue('');
    }

    return (
        <>
            <Form inline onSubmit={onSubmit}>
                <FormControl 
                    type="text" 
                    placeholder="Nome ou endereço" 
                    className="mr-sm-2" 
                    value={value}
                    onChange={(e) => {setValue(e.target.value)}}
                />
                <Button variant="outline-success" type="submit" disabled={value===""}>Buscar</Button>
            </Form> 
        </>
    )
}
