import React, { useEffect, useState } from 'react';
import { Row, Col, Container, Button, Card,Tab, ListGroup, Spinner } from 'react-bootstrap';
import { FaTrash } from 'react-icons/fa';

import MapComponent from './components/Map';
import SearchVeiculo from './components/SearchVeiculo';
import SearchParada from './components/SearchParada';
import Contador from './components/Contador';
import VeiculoDetails from './components/VeiculoDetails';
import ParadaDetails from './components/ParadaDetails';
import TooltipComponent from '../components/Tooltip';

export default function Mapa(props) {

    const { api } = props;

    const [veiculos, setVeiculos] = useState([]);
    const [paradas, setParadas] = useState([]);
    const [mostrarVeiculos, setMostrarVeiculos] = useState(false);
    const [mostrarParadas, setMostrarParadas] = useState(false);
    const [atualizacaoHora, setAtualizacaoHora] = useState(null);
    const [veiculoBuscado, setVeiculoBuscado] = useState(null);
    const [atualizar, setAtualizar] = useState(false);


    const [parada, setParada] = useState(null);

    function atualizarDados() {
        loadVeiculos();
        if (veiculoBuscado) {
            veiculos.forEach(veiculo => {
                if (veiculoBuscado.c == veiculo.c) {
                    setVeiculoBuscado(veiculo);
                }
            })
        }
    }

    function handleDelete() {
        setVeiculoBuscado(null);
        setAtualizar(false);
    }

    function loadVeiculos() {

        api.get('Posicao')
            .then(response => {
                setAtualizacaoHora(response.data.hr);
                setVeiculos(response.data.l);
            })
            .catch(function (error) {
                console.log("erro")
            })
    }
    function loadParadas() {

        api.get('Paradas/Buscar?termosBusca=*')
            .then(response => {
                setParadas(response.data.l);
            })
            .catch(function (error) {
                console.log("erro")
            })
    }

    useEffect(() => {
        loadVeiculos();
        loadParadas();
    }, [])


    return (
        <Container className="section-2" style={{ marginTop: 30, marginBottom: 30 }}>
            {atualizacaoHora ?
                <>
                    <Row style={{
                        display: "flex",
                        justifyContent: "center"
                    }}>
                        <h1>Ver no mapa</h1>
                    </Row>
                    <Row style={{
                        marginTop: 30,
                        width: "100%"
                    }}>
                        <Col md={5} xs={12} style={{
                            justifyContent: "center",
                            marginTop: 20
                        }}>
                        <Tab.Container id="list-group-tabs-example" defaultActiveKey="#link1">
                            <Row>
                                <Col sm={4}>
                                    <ListGroup>
                                        <ListGroup.Item variant="success" action href="#link1">
                                            Link 1
                                    </ListGroup.Item> 
                                        <ListGroup.Item variant="success" action href="#link2">
                                            Link 2
                                </ListGroup.Item>
                                    </ListGroup>
                                </Col>
                                <Col sm={8}>
                                    <Tab.Content>
                                        <Tab.Pane eventKey="#link1">
                                        </Tab.Pane>
                                        <Tab.Pane eventKey="#link2">
                                        </Tab.Pane>
                                    </Tab.Content>
                                </Col>
                            </Row>
                        </Tab.Container>
                        </Col>
                    </Row>
                </>
                :

                <Container style={{
                    display: "flex",
                    justifyContent: "center"
                }}>
                    <Spinner animation="border" variant="success" />
                </Container>}
        </Container>
    )
}
