import React, {useState, useEffect} from 'react';
import {Form, FormControl, Button} from 'react-bootstrap';
import {FaPlus, FaMinus} from 'react-icons/fa'
import api from '../../../../../../../../services/api'

export default function Search(props){
    const { setResultados, atualizar }  = props;
    const [paradaValue, setParadaValue] = useState('');
    const [linhaValue, setLinhaValue] = useState('');
    const [selectValue, setSelectValue] = useState('');


    function onSubmit(e){
        e.preventDefault();
        if(selectValue === "1"){

            api.get(`Previsao/Parada?codigoParada=${paradaValue}`)
            .then(response => {
                setResultados(response.data);
            }).catch(function(error){
                //error
            })


        }else if(selectValue === "3"){
            api.get(`Previsao?codigoParada=${paradaValue}&codigoLinha=${linhaValue}`)
            .then(response => {
                setResultados(response.data);
            }).catch(function(error){
                //error
            })
        }
        setParadaValue('');
    }

    useEffect(() => {
        onSubmit();
    }, [atualizar])

    return (
        <>
            <Form inline>
                <Form.Control 
                    as="select"  
                    size="lg"
                    value={selectValue}
                    onChange={(e) => {setSelectValue(e.target.value)}}
                >
                    <option hidden>Selecione uma opção</option>
                    <option value="1">Pesquisar por parada</option>
                    <option value="2">Pesquisar por linha</option>
                    <option value="3">Pesquisar por parada e linha</option>
                </Form.Control>
            </Form> 
            <Form inline onSubmit={onSubmit}>
                {
                    (selectValue === "1" || selectValue === "3") &&
                    <FormControl 
                    type="text" 
                    placeholder="Código da parada" 
                    value={paradaValue}
                    onChange={(e) => {setParadaValue(e.target.value)}}
                    className="mr-sm-2" 
                />
                }
                {
                    (selectValue === "2" || selectValue === "3") &&
                    <FormControl 
                    type="text" 
                    placeholder="Código da linha" 
                    value={linhaValue}
                    onChange={(e) => {setLinhaValue(e.target.value)}}
                    className="mr-sm-2" 
                />
                }

                { selectValue !== "" && <Button variant="outline-success" type="submit" >Buscar</Button>}
            </Form>
        </>
    )
}
