import React, {useRef, useEffect} from 'react';
import { Map, TileLayer } from 'react-leaflet';
import { Marker, Popup } from 'leaflet';
import {pointerBusIcon} from './components/Icons';

const data = {
    "hr": "20:20",
    "p": {
      "cp": 4200953,
      "np": "PARADA ROBERTO SELMI DEI B/C",
      "py": -23.675901,
      "px": -46.752812,
      "l": [
        {
          "c": "675K-10",
          "cl": 198,
          "sl": 1,
          "lt0": "METRO STA CRUZ",
          "lt1": "TERM. JD. ANGELA",
          "qv": 1,
          "vs": [
            {
              "p": "73651",
              "t": "23:22",
              "a": true,
              "ta": "2017-05-07T23:20:06Z",
              "py": -23.676623333333335,
              "px": -46.757641666666665
            }
          ]
        }
      ]
    }
  }

export default function MapComponent(props) {
    const { results } = props;

    const mapRef = useRef();
    const didMountRef = useRef(false) 
    useEffect(() => {
        if (didMountRef.current) { 
        if (mapRef.current) {
            mapRef.current.leafletElement.invalidateSize(false);
        }
        } 
        else didMountRef.current = true
    })

    return (
        <Map center={[-23.692865, -46.77835]} zoom={12} ref={mapRef} >
            <TileLayer
                attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />
            {data.p &&
            <Marker position={[data.p.py, data.p.px]} icon={pointerBusIcon}>
                <Popup>
                    {data.p.np}
                </Popup>
            </Marker>
            }
        </Map>
    )
}
