import React, {useState} from 'react';
import {Form, FormControl, Button} from 'react-bootstrap';
import {FaPlus, FaMinus} from 'react-icons/fa'
import api from '../../../../../../../../services/api'

export default function Search(props){
    const { setParadas, veiculo, mostrarParadas, setMostrarParadas} = props;
    const [value, setValue] = useState('');
    const [selectValue, setSelectValue] = useState('');

    const [exibirFiltros, setExibirFiltros] = useState(false);

    function onSubmitSelect(e){
        e.preventDefault();

    }

    return (
        <>
            <Form inline>
                <Form.Control 
                    as="select"  
                    size="lg"
                    value={selectValue}
                    onChange={(e) => {setSelectValue(e.target.value)}}
                >
                    <option hidden>Selecione uma opção</option>
                    <option value="1">Pesquisar por parada</option>
                    <option value="2">Pesquisar por linha</option>
                    <option value="3">Pesquisar por parada e linha</option>
                </Form.Control>
            </Form> 
            {
                selectValue === "1" && <Form inline>
                <FormControl 
                    type="text" 
                    placeholder="Buscar veículos" 
                    className="mr-sm-2" 
                />
                <Button variant="outline-success" type="submit" disabled={value===""}>Buscar</Button>
            </Form>
            }
        </>
    )
}
